};
template< typename M >
struct Matrix{
	using T = typename M::t;
	int H;
	int W;
	vector<T> data;
    Matrix(int n_)
        : H(n_), W(n_), data(n_ * n_, M::id()) {}
    Matrix(int h_, int w_)
        : H(h_), W(w_), data(h_ * w_, M::id()) {}
	Matrix(int n_,vector<T> d_)
        : H(n_), W(n_) {data = d_;}
    Matrix(int h_, int w_, vector<T> d_)
        : H(h_), W(w_) {data = d_;}
  	void Update(int h,int w,T x){
    	data[h*W+w] = x;
  	}
  	const T Get(int h,int w){
    	return data[h*W+w];
  	}
	int Rank(){
		int ans = 0;
		vector<T> A(H*W);
		for(int i=0;i<H*W;i++){
			A[i] = data[i];
		}
		for(int i=0;i<H;i++){
			int nonzero = -1;
			for(int j=i;j<W;j++){
				if(A[j*W+i] != M::id()){
					nonzero = j;
					ans++;
					break;
				}
			}
			if(nonzero != -1){
				for(int j=i;j<W;j++){
					T temp = A[nonzero*W+j];
					A[nonzero*W+j] = A[i*W+j];
					A[i*W+j] = temp;
				}
				for(int j=i+1;j<H;j++){
					T d = M::op3(M::op1(M::op4(A[i*W+i]),A[j*W+i])); 
					for(int k=i;k<W;k++){
						A[j*W+k] = M::op2(A[j*W+k],M::op1(A[i*W+k],d));
					}
				}
			}
		}	
		return ans;
	}
	static Matrix I(int N){
		Matrix ans(N);
		for(int i=0;i<N;i++){
			ans.Update(i,i,M::e());
		}
		return ans;
	}
	static Matrix Plus(Matrix& A,Matrix& B){
		int h = A.H;
		int w = A.W;
		Matrix ans(h,w);
		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				ans.Update(i,j,M::op2(A.Get(i,j),B.Get(i,j)));
			}
		}
		return ans;
	}
	static Matrix Minus(Matrix& A){
		int h = A.H;
		int w = A.W;
		Matrix ans(h,w);
		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				ans.Update(i,j,M::op3(A.Get(i,j)));
			}
		}
		return ans;
	}
	static Matrix Minus(Matrix& A,Matrix& B){
		int h = A.H;
		int w = A.W;
		Matrix ans(h,w);
		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				ans.Update(i,j,M::op2(A.Get(i,j),M::op3(B.Get(i,j))));
			}
		}
		return ans;
	}
	static Matrix Prod(Matrix& A,Matrix& B){
		int h = A.H;
		int w = B.W;
		int K = A.W;
		Matrix ans(h,w);
		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				T c = M::id();
				for(int k=0;k<K;k++){
					c = M::op2(c,M::op1(A.Get(i,k),B.Get(k,j)));
				}
				ans.Update(i,j,c);
			}
		}
		return ans;
	}
	static Matrix Pow(Matrix A,long r){
		int n = A.H;
		Matrix ans = I(n);
		while(r){
			if(r % 2 == 1){
				ans = Prod(ans,A);
			}
			A = Prod(A,A);
			r /= 2;
		}
		return ans;
	}
};
void Calc(){
	int N = rei();
	int M = rei();
	vector<bool> A(N*M);
	for(int i=0;i<N;i++){
		for(int j=0;j<M;j++){
			int x = rei();
			A[i*M+j] = x % 2 == 1;
		}
	}
	Matrix<MTwo> mat(N,M,A);
	int r = mat.Rank();
	long ans1 = 1;
	long ans2 = 1;
	for(int i=0;i<N+M-1;i++){
		ans1 *= 2;
		ans1 %= mod;
	}
	for(int i=0;i<N+M-r-1;i++){
		ans2 *= 2;
		ans2 %= mod;
	}
	cout << (ans1 - ans2 + mod) % mod << endl;
}
