#define repx(i, a, b) for(int i = a; i < b; i++)
const LL MOD = 998244353;
const int MAXN = 310;
bitset<MAXN> bb[MAXN];

LL pw(LL a, LL b){
    LL ans = 1;
    while(b){
        while(!(b & 1)) b >>= 1, a = (a * a) % MOD;
        ans = (ans * a) % MOD, --b;
    }
    return ans;
}

int main(){
    int n, m, x;
    scanf("%d %d", &n, &m);
    rep(i, n){
        rep(j, m){
            scanf("%d", &x);
            bb[i][j] = x;
        }
    }
    int now = 0;
    rep(i, m){
        int fl = 0;
        repx(j, now, n){
            if(bb[j][i]){
                fl = 1;
                swap(bb[j], bb[now]);
                break;
            }
        }
        if(!fl) continue;
        repx(j, now + 1, n) if(bb[j][i]) bb[j] ^= bb[now];
        ++now;
    }
    LL go = (pw(2, n) + MOD - pw(2, n - now)) % MOD;
    printf("%lld\n", (go * pw(2, m - 1)) % MOD);
    return 0;
}

