ll powMod(ll x, ll n, ll mod) {
	ll res = 1;
	while (n > 0) {
		if (n & 1) res = res * x % mod;
		x = x * x % mod;
		n >>= 1;
	}
	return res;
}

int matrix_rank(vvi m) {
	int H = m.size();
	int W = m[0].size();
	int r = 0;	// 
	rep(i, W) {
		if (m[r][i] == 0) {
			bool find = false;
			repr(j, r + 1, H) {
				if (m[j][i] == 1) {
					swap(m[r], m[j]);
					find = true;
					break;
				}
			}
			if (!find)continue;
		}
		repr(j, r + 1, H) {
			if (m[j][i] == 1) {
				repr(k, i, W) {
					m[j][k] ^= m[r][k];
				}
			}
		}
		r++;
		if (r == H)break;
	}
	return r;
}

