const int NN=301;
bool solve(bool p[NN][NN], int n, int m){
	int s=0;
	for(int i=0;i<m;i++){
		int d=-1;
		for(int j=s;j<n;j++){
			if(p[j][i]){
				d=j;
			}
		}
		if(d==-1){
			continue;
		}
		if(d!=i){
			for(int j=i;j<m;j++){
				swap(p[i][j],p[d][j]);
			}
		}
		for(int j=s+1;j<n;j++){
			if(!p[j][i])continue;
			for(int k=i;k<n+1;k++){
				p[j][k]^=p[i][k];
			}
		}
		s++;
	}
	return s;
}

