template <typename T>
using Matrix = vector< vector<T> >;

template <typename T>
void init_mat(Matrix<T> &A, int h, int w) {
    A.resize(h, vector<T>(w, 0));
}

template <typename T>
Matrix<T> calc_mat(Matrix<T> A, Matrix<T> B) {
    Matrix<T> C(A.size(), vector<T>(B[0].size()));
    for(int i=0; i<A.size(); i++) {
        for(int k=0; k<B.size(); k++) {
            for(int j=0; j<B[0].size(); j++) {
                // C[i][j] += A[i][k] * B[k][j]; // mod
                C[i][j] = (C[i][j] + A[i][k] * B[k][j]) % MOD; // mod
            }
        }
    }
    return C;
}

template <typename T>
Matrix<T> mat_pow(Matrix<T> A, ll n) {
    Matrix<T> B(A.size(), vector<T>(A.size()));
    for(int i=0; i<A.size(); i++) B[i][i] = 1;
    while(n > 0) {
        if(n & 1) B = calc_mat(B, A);
        A = calc_mat(A, A);
        n >>= 1;
    }
    return B;
}

// Matrix Library End

// N * M 
template <typename T>
int mat_rank(Matrix<T> A) {
    int N = A.size(), M = A[0].size();

    int WID = min(N, M), res = 0;
    for(int i=0; i<WID; i++) {
        // 
        T max_elem = 0; int piv = -1;
        for(int j=res; j<N; j++) {
            if(abs(A[j][i]) > max_elem) {
                max_elem = abs(A[j][i]);
                piv = j;
            }
        }

        // fprintf(stderr, "# piv = %lld\n", piv);
        if(piv < 0) continue;

        // res  piv 
        swap(A[res], A[piv]);

        // res 
        // 
        //  double 
        for(int k=0; k<M; k++) {
            A[res][k] = (T)(1.0 * A[res][k] / A[res][i]);
        }

        for(int j=0; j<N; j++) {
            if(j == res) continue;
            assert(A[res][i] != 0);
            T mul = A[j][i];
            for(int k=0; k<M; k++) {
                A[j][k] = (T)(A[j][k] - mul * A[res][k]);
            }
        }
        res++;

        /*
        fprintf(stderr, "# matrix debug\n");
        for(int x=0; x<N; x++) {
            for(int y=0; y<M; y++) {
                fprintf(stderr, "%lld ", A[x][y]);
            }
            fprintf(stderr, "\n");
        }
        */
    }
    return res;
}

