// N * M 
template <typename T>
int mat_rank(Matrix<T> A) {
    int N = A.size(), M = A[0].size();

    int WID = min(N, M), res = 0;
    for(int i=0; i<WID; i++) {
        // 
        T max_elem = 0; int piv = -1;
        for(int j=res; j<N; j++) {
            if(abs(A[j][i]) > max_elem) {
                max_elem = abs(A[j][i]);
                piv = j;
            }
        }

        // fprintf(stderr, "# piv = %lld\n", piv);
        if(piv < 0) continue;

        // res  piv 
        swap(A[res], A[piv]);

        /*
        fprintf(stderr, "# matrix debug\n");
        for(int x=0; x<N; x++) {
            for(int y=0; y<M; y++) {
                fprintf(stderr, "%lld ", A[x][y]);
            }
            fprintf(stderr, "\n");
        }
        */

        for(int j=0; j<N; j++) {
            if(j == res) continue;
            assert(A[res][i] != 0);
            double ratio = 1.0 * A[j][i] / A[res][i];
            for(int k=0; k<M; k++) {
                A[j][k] = (T)(A[j][k] - ratio * A[res][k]);
            }
        }
        res++;
    }
    return res;
}

