using vec = vector<int>;
using mat = vector<vec>;

int mat_rank(mat &A){
    int m = sz(A), n = sz(A[0]);
    int now = 0, ret = 0;
    rep(j, n){
        int pivot = now;
        rep2(i, now, m-1){
            if(A[i][j] > A[pivot][j]) pivot = i; 
        }
        swap(A[now], A[pivot]);
        if(A[now][j] == 0) continue;
        ret++;
        rep(i, m){
            if(i == now) continue;
            rep2(k, j+1, n-1) A[i][k] ^= (A[i][j]*A[now][k]);
            A[i][j] = 0;
        }
        now++;
        if(now == m) break;
    }
    return ret;
}

