int GJ(mat &A) {
	int N = A.size(), M = A[0].size();
	int rnk = 0;
	rep(i, M) {
		if (i == M - 1)break;
		int pivot = -1;
		for (int j = rnk; j < N; j++) {
			if (A[i][j]) {
				pivot = j; break;
			}
		}
		if (pivot == -1)continue;
		swap(A[pivot], A[rnk]);

		rep(j, N) {
			if (j != rnk&&A[j][i]) {
				rep(k, M) {
					A[j][k] ^= A[rnk][k];
				}
			}
		}
		rnk++;
	}

	int res = 0;
	rep(i, N) {
		rep(j, M) {
			if (A[i][j]) {
				res++; break;
			}
		}
	}
	return res;
}

