// N * M 
template <typename T>
int mat_rank(Matrix<T> A) {
    int N = A.size(), M = A[0].size();

    for(int i=0; i<N; i++) {
        {
            //  (i )
            T nonzero = 0; int piv = -1;
            for(int j=i; j<N; j++) {
                for(int k=0; k<M; k++) {
                    if(A[j][k] != 0) {
                        nonzero = A[j][k];
                        piv = k;
                    }
                }
            }
            // 
            if(nonzero == 0) return i;

            // i  piv 
            for(int j=0; j<N; j++) {
                swap(A[j][i], A[j][piv]);
            }
        }

        {
            // i 
            T max_elem = 0; int piv = -1;
            for(int j=i; j<N; j++) {
                if(abs(A[j][i]) > max_elem) {
                    max_elem = abs(A[j][i]);
                    piv = j;
                }
            }

            // i  piv 
            swap(A[i], A[piv]);

            /*
            for(int x=0; x<N; x++) {
                for(int y=0; y<M; y++) {
                    fprintf(stderr, "%lld ", A[x][y]);
                }
                fprintf(stderr, "\n");
            }
            */

            for(int j=0; j<N; j++) {
                if(i == j) continue;
                assert(A[i][i] != 0);
                T ratio = 1.0 * A[j][i] / A[i][i];
                for(int k=0; k<N; k++) {
                    A[j][k] = A[j][k] - ratio * A[i][k];
                }
            }
        }
    }
    return N;
}

