		for (int j = pos - 1; j >= 0; j--) {
			if (A[j][i] == 0) continue;
			for (int k = 0; k < W; k++) A[j][k] ^= A[pos][k];
		}
		sort(A, A + H);
	}

	vector<int> ZERO(W, 0);
	long long v = 1;
	for (int i = 0; i < H; i++) {
		if (A[i] == ZERO) { v *= 2; v %= mod; }
	}
	long long p = 1; for (int i = 0; i < H; i++) { p *= 2; p %= mod; }
	long long q = 1; for (int i = 0; i < W; i++) { q *= 2; q %= mod; }
	long long r = (p - v + mod)*q%mod;
	if (r % 2 == 1) r += mod; r /= 2;
	cout << r << endl;
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

#define MOD 998244353

vector< vector<int> > A;

long long power(long long N, long long expo)
{
    long long ans = 1;

    while(expo)
    {
        if(expo % 2)
            ans = ans * N % MOD;

        N = N * N % MOD;
        expo /= 2;
    }

    return ans;
}

int main()
{
    ios::sync_with_stdio(0);
    cin.tie(NULL), cout.tie(NULL);

    int N, M;
    cin >> N >> M;

    for(int i=0; i<N; i++)
    {
        vector<int> B(M, 0);
        A.push_back(B);

        for(int j=0; j<M; j++)
        {
            int x;
            cin >> x;

            A[i][j] = x;
        }
    }

    long long ans = power(2, N) + MOD - 1;

    for(int i=0; i<N; i++)
    {
        int f = -1;
        sort(A.begin()+i, A.end(), greater< vector<int> >());

        for(int j=0; j<M; j++)
        {
            if(A[i][j] == 1)
            {
                f = j;
                break;
            }
        }

        if(f == -1)
        {
            ans += MOD - power(2, N-i) + 1;
            break;
        }

        for(int j=i+1; j<N; j++)
        {
            if(A[j][f] == 1)
            {
                for(int k=f; k<M; k++)
                    A[j][k] ^= A[i][k];
            }
        }
    }

    cout << ans % MOD * power(2, M-1) % MOD << "\n";
    return 0;
}

