// >>> gauss jordan for vector<bitset>
template <size_t N>
int gauss_jordan(vector<bitset<N>> &a) {
    const int h = a.size();
    int i = 0;
    for (int j = 0; i < h && j < (int)N; j++) {
        int k = i; while (k < h && !a[k][j]) k++;
        if (k == h) continue;
        swap(a[i],a[k]);
        rep (k,h) if (k != i && a[k][j]) a[k] ^= a[i];
        i++;
    }
    return i;
}
