    rep(i,0,N){
        bool t=false;;
        rep(j,0,M){
            if(a[i][j]==1) t=true;
        }
        if(t) ran++;
    }
    cout <<(mod_pow(2LL, N+M-1, MOD)-mod_pow(2LL, N+M-1-ran, MOD)+MOD)%MOD<<"\n";
}

