#define MOD   998244353LL

bool swap_row(ll a[303][303], ll rows, ll cols, ll pos_row, ll pos_col)
{
    ll dpos_row = pos_row;
    while (!a[dpos_row][pos_col] && dpos_row < rows) dpos_row++;
    if (dpos_row == rows) return false;
    if (dpos_row == pos_row) return true;

    rep (i, cols) {
        ll tmp = a[pos_row][i];
        a[pos_row][i] = a[dpos_row][i];
        a[dpos_row][i] = tmp;
    }

    return true;
}

ll calc_rank(ll a[303][303], ll rows, ll cols)
{
    ll pos_row = 0LL;

    rep (pos_col, cols) {
        bool found = swap_row(a, rows, cols, pos_row, pos_col);
        if (!found) continue;

        srep (row, pos_row+1LL, rows) {
            if (!a[row][pos_col]) continue;
            rep (col, cols) {
                a[row][col] -= a[pos_row][col];
                a[row][col] &= 1LL;
            }
        }

        pos_row++;
        if (pos_row >= rows) break;

        debug_printf("---- pos_row=%lld\n", pos_row);
        rep (i, rows) {
            rep (j, cols) debug_printf("%lld ", a[i][j]);
            debug_printf("\n");
        }

    }

    debug_printf("---- gauss a\n");
    rep (i, rows) {
        rep (j, cols) debug_printf("%lld ", a[i][j]);
        debug_printf("\n");
    }

    ll rank = 0LL;

    rep (row, rows) {
        rep (col, cols) {
            if (a[row][col]) {
                rank = row + 1LL;
                break;
            }
        }
    }

    debug_printf("---- rank = %lld\n", rank);
    return rank;
}

int _main()
{
    ll rows; llin(rows);
    ll cols; llin(cols);
    static ll a[303][303];
    rep (i, rows) rep (j, cols) llin(a[i][j]);

    ll colcnt_all = mod_pow(2LL, cols, MOD);
    debug_printf("colcnt_all = %lld\n", colcnt_all);

    ll rank = calc_rank(a, rows, cols);

    // rank + dim(kernel_set) == cols
    ll dim_kernel_set = cols - rank;

    ll colcnt_invalid = mod_pow(2LL, dim_kernel_set, MOD);
    ll colcnt_valid = colcnt_all - colcnt_invalid;
    debug_printf("---- colcnt_valid = %lld\n", colcnt_valid);

    ll ans = colcnt_valid * mod_pow(2LL, rows, MOD);
    ans %= MOD;
    ans *= mod_inv(2LL, MOD);
    ans %= MOD;

    cout << ans << endl;

    return 0;
}

