                        swap(b[i],it);
                    }
                    break;
                }
            }
        }
        if(b[i].count()!=0){
            v.pb(b[i]);
            tot--;
        }
    }
    LL ans=(f_pow(2,n)-f_pow(2,tot))*f_pow(2,m-1)%mod;
    ans=((ans+mod)%mod);
    printf("%lld\n",ans);
}
