typedef vector<vec> mat;
const double EPS=1E-8;//
int mat_rank(mat A) {
    int N = A.size(), M = A[0].size();
    int res = 0;
    for(int i=0; i<M; i++) {
        // 
        double max_elem = 0; int piv = -1;
        for(int j=res; j<N; j++) {
            if(abs(A[j][i])-EPS > max_elem) {
                max_elem = abs(A[j][i]);
                piv = j;
            }
        }
        if(piv < 0) continue;
        swap(A[res], A[piv]);
        for(int k=0; k<M; k++) {
            A[res][k] = (1.0 * A[res][k] / A[res][i]);
        }
        for(int j=0; j<N; j++) {
            if(j == res) continue;
            double mul = A[j][i];
            for(int k=0; k<M; k++) {
                A[j][k] = A[j][k] - mul * A[res][k];
            }
        }
        res++;
    }
    return res;
}
ll mod_pow(ll x, ll n, ll mod){
    if (n==0) return 1;
    ll res=mod_pow(x*x%mod, n/2, mod);
    if (n%2==1) res=res*x%mod;
    return res;
}
