#define mod 998244353
using namespace std;

int read() {
    int x = 0, f = 1;
    char c = getchar();
    while (c < '0' || c > '9') {
        if (c == '-') f = -1;
        c = getchar();
    }
    while (c >= '0' && c <= '9') {
        x = x * 10 + c - '0';
        c = getchar();
    }
    return x * f;
}

int base[302][302];
int a[302];
int sum, n, m;

long long fsp(int p) {
    long long rt = 1, base = 2;
    while (p) {
        if (p & 1) (rt *= base) %= mod;
        (base *= base) %= mod;
        p >>= 1;
    }
    return rt;
}

void work() {
    for (int i = m; i >= 1; --i) {
        if (!a[i]) continue;
        if (!base[i][i]) {
            sum--;
            for (int j = 1; j <= i; ++j) {
                base[i][j] = a[j];
            }
            for (int j = 1; j < i; ++j) {
                if (!base[i][j]) continue;
                for (int k = 1; k <= j; ++k) {
                    base[i][k] ^= base[j][k];
                }
            }
            for (int j = i + 1; j <= m; ++j) {
                if (!base[j][i]) continue;
                for (int k = 1; k <= i; ++k) {
                    base[j][k] ^= base[i][k];
                }
            }
            break;
        } else {
            for (int j = 1; j <= i; ++j) {
                a[j] ^= base[i][j];
            }
        }
    }
    return ;
}

int main() {
    n = sum = read(), m = read();
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= m; ++j) {
            a[j] = read();
        }
        work();
    }
    printf("%lld", fsp(m - 1) * (fsp(n) + mod - fsp(sum)) % mod);
    return 0;
}

