ll powMod(ll x, ll n, ll mod) {
	ll res = 1;
	while (n > 0) {
		if (n & 1) res = res * x % mod;
		x = x * x % mod;
		n >>= 1;
	}
	return res;
}

int matrix_rank(vvi m) {
	int H = m.size();
	int W = m[0].size();
	int r = 0;	// 
	rep(i, W) {
		if (m[r][i] == 0) {
			bool find = false;
			repr(j, r + 1, H) {
				if (m[j][i] == 1) {
					swap(m[r], m[j]);
					find = true;
					break;
				}
			}
			if (!find)continue;
		}
		repr(j, r + 1, H) {
			if (m[j][i] == 1) {
				repr(k, i, W) {
					m[j][k] ^= m[r][k];
				}
			}
		}
		r++;
		if (r == H)break;
	}
	return r;
}

int main(void) {
	int H, W;
	cin >> H >> W;
	vvi m(H, vi(W));
	cin >> m;
	int r = matrix_rank(m);
	int k = W - r;
	ll ans = powMod(2, W, MOD2) - powMod(2, k, MOD2) + MOD2;
	ans %= MOD2;
	ans *= powMod(2, H - 1, MOD2);
	ans %= MOD2;
	cout << ans << endl;
}

