const ll MOD = 998244353;

//  O(min(H,W)HW/64)
// bitsetbit
using mat = vector<bitset<300>>;
mat gauss_jordan(mat a) {
    const int h = a.size(), w = a[0].size();
    int rank = 0;
    REP(i, min(w, h)) {
        int pivot = -1;
        FOR(j, rank, h) if(a[j][i]) {pivot = j; break;}
        if(pivot == -1) continue;
        swap(a[rank], a[pivot]);
        REP(j, h) if(j != rank && a[j][i]) a[j] ^= a[rank];
        rank++;
    }
    return a;
}

//  O(func*log(e))
template<typename T=ll, typename F=function<T(T,T)>>
T binpow(T x, ll e, F func=[](T a, T b){return a*b%MOD;}, T d=1) {
  T ret = d, p = x;
  while(e > 0) {
    if(e&1) {ret = func(ret, p); e--;}
    else {p = func(p, p); e /= 2;} 
  }
  return ret;
};

signed main(void)
{
    cin.tie(0);
    ios::sync_with_stdio(false);

    ll h, w;
    cin >> h >> w;
    mat a(h);
    REP(i, h) REP(j, w) {
        ll b; cin >> b;
        if(b) a[i].set(j);
    }

    auto ret = gauss_jordan(a);
    ll rank = h;
    REP(i, h) {
        bool flag = false;
        REP(j, w) if(ret[i][j]) flag = true;
        if(!flag) {
            rank = i;
            break;
        }
    }

    // (2^h - 2^(h-rank)) * 2^(w-1)
    ll ans = ((binpow(2, h) - binpow(2, h-rank)) % MOD + MOD) % MOD;
    (ans *= binpow(2, w-1)) %= MOD;
    cout << ans << endl;

    return 0;
}

