template <typename T>
using Matrix = vector< vector<T> >;

template <typename T>
void init_mat(Matrix<T> &A, int h, int w) {
    A.resize(h, vector<T>(w, 0));
}

template <typename T>
Matrix<T> calc_mat(Matrix<T> A, Matrix<T> B) {
    Matrix<T> C(A.size(), vector<T>(B[0].size()));
    for(int i=0; i<A.size(); i++) {
        for(int k=0; k<B.size(); k++) {
            for(int j=0; j<B[0].size(); j++) {
                // C[i][j] += A[i][k] * B[k][j]; // mod
                C[i][j] = (C[i][j] + A[i][k] * B[k][j]) % MOD; // mod
            }
        }
    }
    return C;
}

template <typename T>
Matrix<T> mat_pow(Matrix<T> A, ll n) {
    Matrix<T> B(A.size(), vector<T>(A.size()));
    for(int i=0; i<A.size(); i++) B[i][i] = 1;
    while(n > 0) {
        if(n & 1) B = calc_mat(B, A);
        A = calc_mat(A, A);
        n >>= 1;
    }
    return B;
}

// Matrix Library End

// N * M 
//  double 
int mat_rank(Matrix<double> A) {
    int N = A.size(), M = A[0].size();

    int res = 0;
    for(int i=0; i<M; i++) {
        // 
        double max_elem = 0; int piv = -1;
        for(int j=res; j<N; j++) {
            if(abs(A[j][i]) > max_elem) {
                max_elem = abs(A[j][i]);
                piv = j;
            }
        }

        if(piv < 0) continue;

        // res  piv 
        swap(A[res], A[piv]);

        // res 
        for(int k=0; k<M; k++) {
            A[res][k] = 1.0 * A[res][k] / A[res][i];
        }

        for(int j=0; j<N; j++) {
            if(j == res) continue;
            assert(A[res][i] != 0);
            double mul = A[j][i];
            for(int k=0; k<M; k++) {
                A[j][k] = A[j][k] - mul * A[res][k];
            }
        }
        res++;
    }
    return res;
}

