constexpr int MOD = 998244353;
std::size_t N, M;
template <typename T>
std::size_t Rank(std::vector<std::vector<T>> mat)
{
    std::size_t r = 0;
    for (std::size_t c = 0; c < M; c++) {
        if (r == N) { break; }
        std::size_t piv = r;
        for (; piv < N and mat[piv][c] == 0; piv++) {}
        if (piv == N) { continue; }
        std::swap(mat[r], mat[piv]);
        const T p = mat[r][c];
        for (std::size_t i = r; i < N; i++) { mat[i][c] /= p; }
        for (std::size_t j = 0; j < M; j++) {
            if (j == c) { continue; }
            const T p = mat[r][j];
            for (std::size_t i = 0; i < N; i++) { mat[i][j] -= mat[i][c] * p; }
        }
        r++;
    }
    return r;
}

int main()
{
    std::cin >> N >> M;
    std::vector<std::vector<int>> a(N, std::vector<int>(M));
    for (std::size_t i = 0; i < N; i++) {
        for (std::size_t j = 0; j < M; j++) { std::cin >> a[i][j]; }
    }
    const std::size_t R = Rank(a);
    std::vector<int> bp(600, 1);
    for (std::size_t i = 1; i < 600; i++) { bp[i] = bp[i - 1] * 2 % MOD; }
    std::cout << (bp[N + M - 1] - bp[N + M - R - 1] + MOD) % MOD << std::endl;
    return 0;
}

