long long map[301][2][2]; // col, usedrow_mod2, sum_mod2
int N, M;
int a[300][300];

void search(int col, int* mask) {
    map[col][0][0] = 0;
    map[col][0][1] = 0;
    map[col][1][0] = 0;
    map[col][1][1] = 0;
    if (col == M-1) {
        // backtrack
        // use this col
        for (int i=0; i<N; i++) {
            if (mask[i] == 0) continue;
            if (a[col][i] == 1) {
                // use
                map[col][0][0] += map[col+1][1][1];
                map[col][0][1] += map[col+1][1][0];
                map[col][1][0] += map[col+1][0][1];
                map[col][1][1] += map[col+1][0][0];
                // no use
                map[col][0][0] += map[col+1][0][0];
                map[col][0][1] += map[col+1][0][1];
                map[col][1][0] += map[col+1][1][0];
                map[col][1][1] += map[col+1][1][1];
            } else {
                // use
                map[col][0][0] += map[col+1][1][0];
                map[col][0][1] += map[col+1][1][1];
                map[col][1][0] += map[col+1][0][0];
                map[col][1][1] += map[col+1][0][1];
                // no use
                map[col][0][0] += map[col+1][0][0];
                map[col][0][1] += map[col+1][0][1];
                map[col][1][0] += map[col+1][1][0];
                map[col][1][1] += map[col+1][1][1];
            }
            map[col][0][0] %= MOD;
            map[col][0][1] %= MOD;
            map[col][1][0] %= MOD;
            map[col][1][1] %= MOD;
        }
        // wont use this col
        for (int i=0; i<N; i++) {
            if (mask[i] == 0) continue;
            // use
            map[col][0][0] += map[col+1][1][0];
            map[col][0][1] += map[col+1][1][1];
            map[col][1][0] += map[col+1][0][0];
            map[col][1][1] += map[col+1][0][1];
            // no use
            map[col][0][0] += map[col+1][0][0];
            map[col][0][1] += map[col+1][0][1];
            map[col][1][0] += map[col+1][1][0];
            map[col][1][1] += map[col+1][1][1];

            map[col][0][0] %= MOD;
            map[col][0][1] %= MOD;
            map[col][1][0] %= MOD;
            map[col][1][1] %= MOD;
        }
        return;
    }

    int one_mask[300] = {0};
    int zero_mask[300] = {0};
    for (int i=0; i<N; i++) {
        if (mask[i] == 0)continue;
        if (a[col][i] == 1) one_mask[i] = 1;
        else zero_mask[i] = 1;
    }
    search(col+1, one_mask);
    // now map[col+1][:][:] has one_mask data
    // use this col
    map[col][0][0] = map[col+1][0][0];
    map[col][0][1] = map[col+1][0][1];
    map[col][1][0] = map[col+1][1][1];
    map[col][1][1] = map[col+1][1][0];
    // no use
    map[col][0][0] += map[col+1][0][0];
    map[col][0][1] += map[col+1][0][1];
