using namespace std;

using type = double;
using Vec = vector<type>;
using Mat = vector<Vec>;

const double EPS = 1e-6;
bool isZero(type x) { return abs(x) <= EPS; }

// sweep mat[i][j] 0<=i<m && 0<=j<c(default c=n)
// return Vec{j| i,j s.t. minj mat[i][j]!=0}
vector<int> sweep(Mat& mat, int c = -1) {
  if (c == -1) c = mat[0].size();
  int m = mat.size(), n = mat[0].size();
  int row = 0;
  vector<int> res;
  for (int j = 0; j < c; j++) {
    if (row == m) break;
    int tar = -1;
    type v = 0;
    for (int i = row; i < m; i++) {
      if (!isZero(mat[i][j]) && abs(v) < abs(mat[i][j])) {
        tar = i;
        v = mat[i][j];
      }
    }
    if (tar == -1) continue;
    if (row != tar) swap(mat[row], mat[tar]);
    for (int i = 0; i < m; i++) {
      if (i == row) continue;
      if (!isZero(mat[i][j])) {
        type r = mat[i][j] / mat[row][j];
        for (int k = j; k < n; k++) {
          mat[i][k] -= r * mat[row][k];
        }
      }
    }
    res.push_back(j);
    row++;
  }
  return res;
}

type det(Mat mat) {
  sweep(mat);
  type res = 1;
  int sz = mat.size();
  for (int i = 0; i < sz; i++) {
    if (isZero(mat[i][i])) return type(0);
    res *= mat[i][i];
  }
  return res;
}

int myrank(Mat mat) { return sweep(mat).size(); }

