size_t rankF2(vector<bitset<N>>& a, long n, long m){ // calculate rank of matrix in F_2
    size_t now(0), ret(0);
    while(now < m && ret < n){
        size_t i(ret);
        for(; i < n; ++i)if(a[i][now])break;
        if(i == n){
            ++now;
            continue;
