  dp[0][0][0][0][0] = dp[0][0][0][1][0] = dp[0][0][0][0][1] = dp[0][0][a[0][0]][1][1] = 1;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < m; j++) {
      for (int o = 0; o < 2; o++) {
        for (int r = 0; r < 2; r++) {
          for (int c = 0; c < 2; c++) {
            int o2 = r && c ? (o ^ a[i][j]) : o;
            if (i) {
              dp[i][j][o2][r][c] += dp[i - 1][j][o][0][c];
              dp[i][j][o2][r][c] += dp[i - 1][j][o][1][c];
            }
            if (j) {
              dp[i][j][o2][r][c] += dp[i][j - 1][o][r][0];
              dp[i][j][o2][r][c] += dp[i][j - 1][o][r][1];
            }
            if (i && j) {
              dp[i][j][o2][r][c] -= dp[i - 1][j - 1][o][0][0];
              dp[i][j][o2][r][c] -= dp[i - 1][j - 1][o][1][0];
              dp[i][j][o2][r][c] -= dp[i - 1][j - 1][o][0][1];
              dp[i][j][o2][r][c] -= dp[i - 1][j - 1][o][1][1];
            }
            dp[i][j][o2][r][c] %= mod;
          }
        }
      }
    }
  }
  ll t = 0;
  for (int r = 0; r < 2; r++) {
    for (int c = 0; c < 2; c++) {
      t += dp[n - 1][m - 1][1][r][c];
    }
  }
  t %= mod;
  cout << t << endl;
}

