ll qpow(ll a,ll n)
{
    ll ans=1;
    while(n)
    {
        if(n&1) ans=ans*a%mod;
        a=a*a%mod;
        n>>=1;
    }
    return ans;
}
int guass(int n)
{
    int ans=0;
    for(int i=1;i<=n;i++)
    {
        int p=i;
        while(p<=n&&!a[p][i]) p++;
        if(p==n+1) {ans++;continue;}
        swap(a[i],a[p]);
        for(int j=i+1;j<=n;j++)
            if(a[j][i])
            for(int k=i;k<=n;k++)
            a[j][k]^=a[i][k];
    }
    return ans;
}
int main()
{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=n;i++)
        for(int j=1;j<=m;j++)
        scanf("%d",&a[i][j]);
    int s=max(n,m);
    int ans=guass(s);
    if(n>m) ans-=n-m;
    printf("%lld\n",qpow(2,n-1)*(qpow(2,m)+mod-qpow(2,ans))%mod);
}

