template <int MOD>
struct ModInt {
    using lint = long long;
    int val;

    // constructor
    ModInt(lint v = 0) : val(v % MOD) {
        if (val < 0) val += MOD;
    };

    // unary operator
    ModInt operator+() const { return ModInt(val); }
    ModInt operator-() const { return ModInt(MOD - val); }
    ModInt inv() const { return this->pow(MOD - 2); }

    // arithmetic
    ModInt operator+(const ModInt& x) const { return ModInt(*this) += x; }
    ModInt operator-(const ModInt& x) const { return ModInt(*this) -= x; }
    ModInt operator*(const ModInt& x) const { return ModInt(*this) *= x; }
    ModInt operator/(const ModInt& x) const { return ModInt(*this) /= x; }
    ModInt pow(lint n) const {
        auto x = ModInt(1);
        auto b = *this;
        while (n > 0) {
            if (n & 1) x *= b;
            n >>= 1;
            b *= b;
        }
        return x;
    }

    // compound assignment
    ModInt& operator+=(const ModInt& x) {
        if ((val += x.val) >= MOD) val -= MOD;
        return *this;
    }
    ModInt& operator-=(const ModInt& x) {
        if ((val -= x.val) < 0) val += MOD;
        return *this;
    }
    ModInt& operator*=(const ModInt& x) {
        val = lint(val) * x.val % MOD;
        return *this;
    }
    ModInt& operator/=(const ModInt& x) { return *this *= x.inv(); }

    // compare
    bool operator==(const ModInt& b) const { return val == b.val; }
    bool operator!=(const ModInt& b) const { return val != b.val; }

    // I/O
    friend std::istream& operator>>(std::istream& is, ModInt& x) noexcept { return is >> x.val; }
    friend std::ostream& operator<<(std::ostream& os, const ModInt& x) noexcept { return os << x.val; }
};

constexpr int MOD = 998244353;
using mint = ModInt<MOD>;

void bool_elimination(std::vector<std::vector<int>>& mat) {
    int n = mat.size(), m = mat.front().size();

    for (int l = 0; l < n; ++l) {
        {
            auto& v = *std::max_element(mat.begin() + l, mat.end());
            std::swap(v, mat[l]);
        }

        const auto& v = mat[l];
        for (int i = l + 1; i < n; ++i) {
            auto u = mat[i];
            for (int j = 0; j < m; ++j) u[j] ^= v[j];
            if (u < mat[i]) std::swap(u, mat[i]);
        }
    }
}

template <class T>
std::vector<T> vec(int len, T elem) { return std::vector<T>(len, elem); }

void solve() {
    int n, m;
    std::cin >> n >> m;

    auto mat = vec(n, vec(m, 0));
    for (auto& v : mat) {
        for (auto& x : v) {
            std::cin >> x;
        }
    }
    bool_elimination(mat);

    int r = std::count_if(mat.begin(), mat.end(),
                          [](const auto& v) {
                              return any_of(v.begin(), v.end(), [](auto x) { return x; });
                          });

    std::vector<mint> dp{1, 0};
    for (int i = 0; i < r; ++i) {
        std::vector<mint> ndp(2);
        for (int j = 0; j < 2; ++j) {
            ndp[j] = dp[j] * 3 + dp[1 - j];
        }
        std::swap(dp, ndp);
    }

    mint ans = dp[1];
    std::cout << ans * mint(2).pow(n + m - r * 2) << std::endl;
}

int main() {
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
    std::ios::sync_with_stdio(false);

    solve();

    return 0;
}

#include "bits/stdc++.h"

using namespace std;

using type = long double;
using Vec = vector<type>;
using Mat = vector<Vec>;

const double EPS = 1e-12;
bool isZero(type x) { return abs(x) <= EPS; }

// sweep mat[i][j] 0<=i<m && 0<=j<c(default c=n)
// return Vec{j| i,j s.t. minj mat[i][j]!=0}
vector<int> sweep(Mat& mat, int c = -1) {
  if (c == -1) c = mat[0].size();
  int m = mat.size(), n = mat[0].size();
  int row = 0;
  vector<int> res;
  for (int j = 0; j < c; j++) {
    if (row == m) break;
    int tar = -1;
    type v = 0;
    for (int i = row; i < m; i++) {
      if (!isZero(mat[i][j]) && abs(v) < abs(mat[i][j])) {
        tar = i;
        v = mat[i][j];
      }
    }
    if (tar == -1) continue;
    if (row != tar) swap(mat[row], mat[tar]);
    for (int i = 0; i < m; i++) {
      if (i == row) continue;
      if (!isZero(mat[i][j])) {
        type r = mat[i][j] / mat[row][j];
        for (int k = j; k < n; k++) {
          mat[i][k] -= r * mat[row][k];
        }
      }
    }
    res.push_back(j);
    row++;
  }
  return res;
}

type det(Mat mat) {
  sweep(mat);
  type res = 1;
  int sz = mat.size();
  for (int i = 0; i < sz; i++) {
    if (isZero(mat[i][i])) return type(0);
    res *= mat[i][i];
  }
  return res;
}

int myrank(Mat mat) { return sweep(mat).size(); }

using ll = long long;
constexpr ll mod = 998244353;

template <typename T>
T mypow(T a, T b) {
  if (b == 0) return 1;
  T tmp = mypow(a, b / 2);
  if (b % 2)
    return (((tmp * tmp) % mod) * a) % mod;
  else
    return (tmp * tmp) % mod;
}

int main() {
    ll n,m;cin>>n>>m;
    Mat a(n, Vec(m));
    for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            cin >> a[i][j];
        }
    }
    int r = myrank(a);
    ll ret = mypow(2LL, n+m-1) + mod - mypow(2LL, n+m-r-1);
    cout << ret % mod << endl;
}

