const ll MOD=998244353;
ll n,m,a[310][310];

ll ad(ll x,ll y){
  return (x+y)%MOD;
}

ll mlt(ll x,ll y){
  return (x*y)%MOD;
}

ll pwr(ll x,ll y){
  ll pw[32]={},w=1;
  pw[0]=x;
  fr(i,31) pw[i+1]=mlt(pw[i],pw[i]);
  fr(i,32){
    w=mlt(w,1+(y%2)*(pw[i]-1));
    y/=2;
  }
  return w;
}

ll rnk(ll n,ll m,ll M[][310]){
  ll d=0;
  fr(j,m){
    bool f=0;
    if(M[d][j]==0){
      f=1;
      for(ll i=d;i++<n-1;){
        if(M[i][j]!=0){swap(M[d],M[i]);f=0;}
      }
    }
    if(f && j<m-1) continue;
    if(f) return d;
    for(ll i=d;i++<n-1;){
      if(M[i][j]==0) continue;
      for(ll k=j;k<m;k++){
        M[i][k]=(M[i][k]+M[d][k])%2;
      }
    }
    d++;
    if(d==n) return n;
  }
}

