	{
		vector<std::string> v;        // 
		auto first = str.begin();              // 
		while (first != str.end()) {         // 
			auto last = first;                      // 
			while (last != str.end() && *last != sep)       //  or 
				last++;
			v.push_back(string(first, last));       // 
			if (last != str.end())
				last++;
			first = last;          // 
		}
		return v;
