void initEquationSystem() {
	for (int i=0; i<n; ++i) {
		e[i].init(m);
		for (int j=0; j<m; ++j)
			e[i].coeff[j] = a[i][j];
		e[i].val = 0;
	}
	// PR0(e[1].coeff, m);
}

int64_t pw(int n, int k) {
	if (k==0)
		return 1;

	int64_t tmp = pw(n, k/2);

	if (k%2)
		return tmp * tmp % MOD * n % MOD;
	return tmp * tmp % MOD;
}

void solve() {
	for (int i=0; i<n; ++i) {
		for (int j=i+1; j<n; ++j) {
			if (e[i].coeff<e[i].coeff)
				swap(e[i], e[j]);
		}

		int pos = -1;

		for (int j=0; j<m; ++j) {
			if (e[i].coeff[j]) {
				pos = j;
				break;
			}
		}

		for (int j=i+1; j<n; ++j) {
			if (pos>-1 && e[j].coeff[pos]) {
				for (int k=0; k<m; ++k)
					e[j].coeff[k] ^= e[i].coeff[k];
				e[j].val ^= e[i].val;
			}
		}
	}
	// PR0(e[0].coeff, m);

	int tmp = m;

	for (int i=n-1; i>=0; --i)
		tmp -= (accumulate(e[i].coeff.begin(), e[i].coeff.end(), 0)>0);
	// debug(tmp);

	cout << pw(2, n-1) * (pw(2, m) - pw(2, tmp) + MOD) % MOD;
}

