const int MOD = 998244353;

using bs = bitset<300>;

ll solve(vector<bs> v, const int m) {
	const int n = sz(v);

	int removed = 0;
	FOR(j, m) {
		int found = -1;
		for (int i = removed; i < n; i++) {
			if (v[i][j]) {
				found = i;
				break;
			}
		}
		if (found == -1) continue;
		swap(v[removed], v[found]);
		FOR(i, n) {
			if (i != removed && v[i][j]) {
				v[i] ^= v[removed];
			}
		}
		removed++;
	}

	const int freeColRows = n + m - removed * 2;

	Pll ans = { 1,0 };
	FOR(i, removed) {
		Pll nxt;
		nxt.second = (ans.first * 1 + ans.second * 3) % MOD;
		nxt.first = (ans.first * 3 + ans.second * 1) % MOD;
		ans = nxt;
	}
	ll ml = mod_pow(2, freeColRows, MOD);

	return ans.second * ml % MOD;
}

ll naive(vector<bs> v, const int m) {
