typedef long long ll;

const int Mx = 300;
const int mod = 998244353;
ll p2[Mx + 1];

typedef bitset<Mx> * It;

bool bsLess(const bitset<Mx> &b1, const bitset<Mx> &b2) {
	for (int i = Mx - 1; i >= 0; --i)
		if (b1[i] != b2[i])
			return b2[i];
	return false;
}

ll f(It first, It last, const int pos) {
	if (pos < 0 || first == last) {
		assert(all_of(first, last, [](const bitset<Mx> &x) {
			return x == 0;
		}));
		return p2[last - first];
	}

	It pLast = prev(last);
	if ((*pLast)[pos] == 0)
		return f(first, last, pos - 1);
	for (It it = first; it < pLast; ++it)
		if ((*it)[pos])
			*it ^= *pLast;
	return f(first, pLast, pos - 1);
}

bitset<Mx> a[300];

