size_t rankF2(vector<vector<long>>& a){ // calculate rank of matrix in F_2
    long n = a.size(), m = a[0].size();
    size_t now(0), ret(0);
    while(now < m && ret < n){
        size_t i(0);
        for(; i < n; ++i)if(a[i][now])break;
        if(i == n){
            ++now;
            continue;
        }
        size_t j(0);
        for(; j < n; ++j)if(i ^ j && a[j][now])for(int k = 0; k < m; ++k)a[j][k] ^= a[i][k];
        ++ret;
    }
    return ret;
}

