const int base2 = 998244353;

template<typename T>
T binpow(T n, T s)
{
    if (s <= 0)
        return 1LL;
    if (s % 2 == 0){
        T b = binpow(n, s / 2);
        return ( 1LL * b * b ) % base2;
    } else {
        return (1LL* binpow(n, s - 1) * n) % base2;
    }
}

const int N = 305;

int gauss (vector < bitset<N> > a, int n, int m) {    
    int rank = min(n, m);
    vector<char> line_used (n);
    for (int i=0; i<m; ++i) {
        int j;
        for (j=0; j<n; ++j)
            if (!line_used[j] && a[j][i])
                break;
        if (j == n)
            --rank;
        else {
            line_used[j] = true;
            for (int k=0; k<n; ++k)
                if (k != j && a[k][i])
                    a[k] ^= a[j];
        }
    }
    return rank;
}

