constexpr int MOD = 998244353;
inline int quick_pow(int a, int n)
{
	int res = 1;
	while (n)
	{
		if (n & 1)
			res = (ll)res * a % MOD;
		a = (ll)a * a % MOD;
		n >>= 1;
	}
	return res;
}
int fact[305], inv[305], arr[305][305], dp[305];
inline int C(int n, int m) { return (ll)fact[n] * inv[m] % MOD * inv[n - m] % MOD; }
int main()
{
	// freopen("yahoo2019-E.in", "r", stdin);
	int n, m, k = 0, ans = 0;
	scanf("%d%d", &n, &m);
	for (int i = 0; i < n; i++)
	{
		for (int j = 0; j < m; j++)
			scanf("%d", arr[i] + j);
	}
	fact[0] = inv[0] = 1;
	for (int i = 1; i <= m; i++)
	{
		fact[i] = (ll)fact[i - 1] * i % MOD;
		inv[i] = quick_pow(fact[i], MOD - 2);
	}
	for (int col = 0; col < m; col++)
	{
		int row = k;
		while (row < n && !arr[row][col])
			row++;
		if (row >= n)
			continue;
		std::swap(arr[row], arr[k]);
		for (int i = 0; i < n; i++)
		{
			if (!arr[i][col] || i == k)
				continue;
			for (int j = 0; j < m; j++)
				arr[i][j] ^= arr[row][j];
		}
		k++;
	}
	dp[0] = 1;
	for (int i = 0; i < n; i++)
	{
		int sum = 0;
		for (int j = 0; j < m; j++)
			sum += arr[i][j];
		for (int j = m - sum; j >= 0; j--)
			(dp[j + sum] += dp[j]) %= MOD;
	}
	for (int i = 0; i <= m; i++)
	{
		int coef = 0;
		for (int j = 1; j <= i; j += 2)
			(coef += C(i, j)) %= MOD;
		ans = (ans + (ll)dp[i] * coef % MOD * quick_pow(2, m - i)) % MOD;
	}
	printf("%d\n", ans);
	return 0;
}

