typedef long long ll;

const ll MOD = 998244353;

ll modpow(ll x, ll n){
    ll res = 1;
    ll mul = x;
    while(n){
        if(n % 2) res = res * mul % MOD;
        mul = mul * mul % MOD;
        n /= 2;
    }
    return res;
}

struct Combination
{
    int maxn;
    vector<ll> fmemo;
    vector<ll> imemo;
    vector<ll> ifmemo;

    Combination(int maxn) : maxn(maxn){
        fmemo.resize(maxn + 1);
        imemo.resize(maxn + 1);
        ifmemo.resize(maxn + 1);
        for(ll i = 1; i <= maxn; i++){
            imemo[i] = modpow(i, MOD - 2);
        }
    }

    ll fact(ll n){
        if(n == 0) return 1;
        if(fmemo[n]) return fmemo[n];
        return fmemo[n] = n * fact(n - 1) % MOD;
    }

    ll ifact(ll n){
        if(n == 0) return 1;
        if(ifmemo[n]) return ifmemo[n];
        return ifmemo[n] = imemo[n] * ifact(n - 1) % MOD;
    }

    ll com(ll n, ll k){
        if(k > n) return 0;
        if(k == n || k == 0) return 1;
        return (fact(n) * ifact(k) % MOD) * ifact(n - k) % MOD;
    }
};

int main()
{
    int n, m;
    cin >> n >> m;
    int a[302][302];
    for(int i = 0; i < n; i++){
        for(int j = 0; j < m; j++) cin >> a[i][j];
    }
    bool b[302]{0};
    for(int j = 0; j < m; j++){
        for(int i = 0; i < n; i++){
            if(b[i]) continue;
            if(a[i][j]){
                b[i] = true;
                for(int k = i + 1; k < n; k++){
                    if(!a[k][j]) continue;
                    for(int l = j; l < m; l++) a[k][l] ^= a[i][l];
                }
                break;
            }
        }
    }
    int r = 0;
    for(int i = 0; i < n; i++) r += b[i];
    Combination com(n + m + 1);
    ll ans = 0;
    for(int j = 1; j <= r; j += 2){
        ans = (ans + com.com(r, j) * modpow(3, r - j) % MOD * modpow(2, n + m - r * 2)) % MOD;
    }
    cout << ans << endl;
}

