using MMK = IntMod<998244353>;

int N, M;
int A[300][300];
MMK Naive() {
	MMK ret = 0;
	REP(s, 0, 1 << N) {
		REP(t, 0, 1 << M) {
			int cnt = 0;
			REP(i, 0, N) {
				REP(j, 0, M) {
					if (!((1 << i) & s)) continue;
					if (!((1 << j) & t)) continue;
					cnt += A[i][j];
				}
			}
			if (cnt % 2 == 1) {
				++ret;
			}
		}
	}
	return ret;
}

void Swap(int a, int b) {
	REP(j, 0, M) {
		swap(A[a][j], A[b][j]);
	}
}

void Xor(int a, int b) {
	REP(j, 0, M) {
		A[a][j] ^= A[b][j];
	}
}

int Rank() {
	int top_i = 0;
	REP(j, 0, M) {
		int nz_i = -1;
		REP(i, top_i, N) {
			if (A[i][j] == 1) {
				nz_i = i;
				break;
			}
		}

		if (nz_i == -1) continue;

		Swap(top_i, nz_i);
		REP(i, nz_i + 1, N) {
			if (!A[i][j]) continue;
			Xor(i, top_i);
		}
		++top_i;
	}
	return top_i;
}

