	int r = Rank();
	MMK ans = r == 0 ? 0 : MMK(2)[r - 1] * (MMK(2)[r] - 1) * MMK(2)[N + M - 2 * r];

	//cout << naive_ans << endl;
	cout << ans << endl;
	return 0;
}

