ll solve(int h, int w, vector<vector<bool> > const & a) {
    ll cnt = 0;
    REP (yl, h) {
        vector<int> acc(w + 1);
        REP3 (yr, yl + 1, h + 1) {
            int acc_y = 0;
            REP (x, w) {
                acc_y += a[yr - 1][x];
                acc[x + 1] += acc_y;
            }
            int k[2] = {};
            REP (x, w + 1) {
                bool p = acc[x] & 1;
                cnt += k[not p];
                k[p] += 1;
            }
        }
    }
    return cnt;
}

