#include<bits/stdc++.h>
using namespace std;
typedef long long ll;

const int Mx = 300;
const int mod = 998244353;
ll p2[Mx + 1];

typedef bitset<Mx> * It;

bool bsLess(const bitset<Mx> &b1, const bitset<Mx> &b2) {
	for (int i = Mx - 1; i >= 0; --i)
		if (b1[i] != b2[i])
			return b1[i] < b2[i];
	return false;
}

ll f(It first, It last, const int pos) {
	if (pos < 0)
		return p2[last - first];
	It mid = find_if(first, last, [&pos](const bitset<Mx> &x) {
		return x[pos];
	});
	const ll temp = f(first, mid, pos - 1);
	if (mid == last)
		return temp;
	for (It it = next(mid); it != last; ++it)
		*it ^= *mid;
	return (temp * f(mid + 1, last, pos - 1)) % mod;
}

bitset<Mx> a[300];

int main() {
	ios::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);

	p2[0] = 1;
	for (int i = 1; i <= Mx; ++i)
		p2[i] = (p2[i - 1] * 2) % mod;

	int n, m;
	cin >> n >> m;
	for (int i = 0; i < n; ++i)
		for (int j = 0; j < m; ++j) {
			int x;
			cin >> x;
			a[i][j] = x;
		}

	sort(a, a + n, bsLess);
	ll res = f(a, a + n, m - 1);
	res = (p2[n] - res) * p2[m - 1];
	cout << (res % mod + mod) % mod;
	return 0;
}

