	// assuming mat_N = mat_M
	// by changing here, you can use for graph problems (ex. a[i][j]=min(a[i][k]+a[k][j]))
	mat operator*(const mat &m)const{
		mat ret;
		for(int k=0;k<mat_N;k++)for(int i=0;i<mat_N;i++)for(int j=0;j<mat_N;j++){
			ret.a[i][j]=(ret.a[i][j]+a[i][k]*m.a[k][j])%mod;
		}
		return ret;
	}
};
mat I=mat(1);
mat pw(mat a,long long t){
	mat ret=I;
	while(t){
		if(t&1){
			ret=ret*a;
		}
		t/=2;
		a=a*a;
	}
	return ret;
}
long long pw(long long a,long long b){
	long long ret=1;
	while(b){
		if(b%2)ret=ret*a%mod;
		b/=2;a=a*a%mod;
	}
	return ret;
}
int rnk(mat a){
	int ret=0;
	for(int i=0;i<mat_M;i++){
		int at=-1;
		for(int j=ret;j<mat_N;j++){
			if(a.a[j][i]){
				at=j;break;
			}
		}
		if(!~at)continue;
		if(at!=ret){
			for(int j=0;j<mat_M;j++)swap(a.a[at][j],a.a[ret][j]);
		}
		for(int j=0;j<mat_N;j++){
			if(ret==j)continue;
			if(a.a[j][i]==0)continue;
			for(int k=0;k<mat_M;k++){
				a.a[j][k]=(a.a[j][k]^a.a[ret][k]);
			}
		}
		ret++;
	}
	return ret;
}

