    }
    TVector<T>& operator+=(const TVector<T>& v)
    {
        assert(C == v.C);
        for (std::size_t i = 0; i < C; i++) { table[i] += v[i]; }
        return *this;
    }
    TVector<T>& operator-=(const TVector<T>& v)
