template <typename T>
std::size_t Rank(Matrix<T> mat)
{
    std::size_t r = 0;
    for (std::size_t c = 0; c < mat.C; c++) {
        if (r == mat.R) { return r; }
        std::size_t piv = r;
        for (; piv < mat.R and mat[piv][c] == 0; piv++) {}
        if (piv == mat.R) { continue; }
        std::swap(mat[r], mat[piv]);
        for (std::size_t j = c + 1; j < mat.C; j++) { mat[r][j] /= mat[r][c]; }
        const T p = mat[r][c];
        for (std::size_t i = r; i < mat.R; i++) { mat[i][c] /= p; }
        for (std::size_t i = r + 1; i < mat.R; i++) {
            for (std::size_t j = i + 1; j < mat.C; j++) { mat[i][j] -= mat[r][j] * mat[i][c]; }
        }
        r++;
    }
    return r;
}
