void per(int& ans) {
    ans %= p;
    if (ans < 0) { ans += p; }
}
int count_zero(V<bs>& a) {
    int n = a.size();
    int now_st = 0;
    rep(i, 300) {
        int fir_ze = -1;
        for (int j = now_st; j < n; j++) {
            if (a[j][i]) { fir_ze = j; break; }
        }
        if (fir_ze >= 0) {
            rep(j, n) {
                if (j != fir_ze&&a[j][i]) {
                    a[j] = a[fir_ze] xor a[i];
                }
            }
        }
    }
    int ans = 0;
    rep(i, n) {
        bool fl = true;
        rep(j, 300) {
            if (a[i][j]) { fl = false; break; }
        }
        if (fl) { ans++; }
    }
    return ans;
}
signed main() {
    int n, m; cin >> n >> m;
    V<bs> a(n);
    rep(i, n) {
        rep(j, m) {
            int x; cin >> x;
            a[i][j] = x;
        }
    }
    int cz = count_zero(a);
    int ans = rui(2, n,p) - rui(2, cz,p); per(ans);
    ans*=rui(2, m - 1, p); per(ans);
    cout << ans % p << endl;
    return 0;
}

