const ll MOD=998244353;

int N;
int M;
int mat[314][314];

LL mypow(LL base, LL exp) {
  if (exp == 0) return 1;
  LL res = mypow(base*base%MOD, exp/2);
  if (exp%2) res = res*base%MOD;
  return res;
}

int main() {
  scanf("%d%d", &N, &M);
  rep(i, N) {
    rep(j, M) {
      scanf("%01d", &mat[i][j]);
    }
  }

  rep(k, M) {
    reps(i, k, N) {
      if (!mat[i][k]) continue;
      rep(j, M) {
        swap(mat[i][j], mat[k][j]);
      }

      rep(j, N) {
        if (k == j) continue;
        if (!mat[j][k]) continue;
        rep(l, M) {
          mat[j][l] += mat[k][l];
          mat[j][l] %= 2;
        }
      }
    }
  }

  int rank = 0;
  rep(k, min(M, N)) {
    if (mat[k][k]) rank++;
  }

  LL ans = mypow(2, N+M-1);
  ans += MOD - mypow(2, N+M-1-rank);
  ans %= MOD;
  printf("%lld\n", ans);
}

