const int N = 305, mod = 998244353;
bitset<N>a[N], b[N];

int ksm(int a,int b) {
	int res = 1;
	while (b) {
		if (b & 1) res = 1ll * res * a % mod;
		a = 1ll * a * a % mod;
		b >>= 1;
	}
	return res;
}

int main() {
	int n = read(), m = read(), cnt = 0;
	for (int i = 1; i <= n; ++i) 
		for (int j = 1; j <= m; ++j) a[i][j] = read();
	for (int i = 1; i <= n; ++i) 
		for (int j = 1; j <= m; ++j) 
			if (a[i][j]) {
				if (!b[j][j]) {
					b[j] = a[i];
					cnt ++;
					break;
				}
				else {
					a[i] ^= b[j];
				}
			}
	cout << 1ll * (ksm(2, n) - ksm(2, n - cnt) + mod) % mod * ksm(2, m - 1) % mod;
	return 0;
}

