void gauss()
{
	upper=1;
	for (long long i=1;i<=m;i++)
	{
		pos=-1;
		for (long long j=upper;j<=n;j++)
		{
			if (a[j][i])
			{
				pos=j;
				break;
			}
		}
		if (~pos)
		{
			for (long long j=1;j<=m;j++) swap(a[upper][j],a[pos][j]);
			for (long long j=upper+1;j<=n;j++)
			{
				if (a[j][i])
				{
					for (long long k=i;k<=m;k++)
					{
						a[j][k]^=a[upper][k];
					}
				}
			}
			upper++;
		}
	}
}
