size_t rankF2(vector<vector<long>>& a){ // calculate rank of matrix in F_2
    long n = a.size(), m = a[0].size();
    auto add = [&m](vector<long>& to, vector<long>& from){
        for(int i = 0; i < m; ++i)to[i] ^= from[i];
    };
    size_t now(0), ret(0);
    while(now < m && ret < n){
        size_t i(ret);
        for(; i < n; ++i)if(a[i][now])break;
        if(i == n){
            ++now;
            continue;
        }
        size_t j(i + 1);
        for(; j < n; ++j)if(a[j][now])add(a[j], a[i]);
        swap(a[ret], a[i]);
        ++ret;
    }
    return ret;
}

