    for(int i=0;i<N;i++) for(int j=0;j<M;j++) cin >> A[i][j];
    vec<mint> pow2(max(N,M)+1,1);
    for(int i=1;i<=max(N,M);i++) pow2[i] = pow2[i-1]*2;
    int rank = 0;
    for(int j=0;j<M;j++){
        bool update = false;
        for(int i=0;i<N;i++) if(A[i][j]){
            swap(A[i],A[rank]);
            update = true;
            break;
        }
        if(update){
            for(int i=0;i<N;i++) if(i!=rank){
                for(int k=j;k<M;k++) (A[i][k] += A[rank][k])%=2;
            }
            rank++;
        }
    }
    cout << pow2[N-1]*(pow2[M]-pow2[M-rank]) << "\n";
}

