void solve(){
	int n, m;
	scanf("%d%d", &n, &m);
	REP(i, 0, n) {
		REP(j, 0, m) {
			scanf("%d", &v[i][j]);
		}
	}
	int ind = 0;
	REP(k, 0, m) {
		REP(i, ind, n) {
			if (v[i][k] == 1) {
				REP(j, k, m) {
					swap(v[i][j], v[ind][j]);
				}
				break;
			}
		}
		if (v[ind][k] == 0) {
			continue;
		}
		REP(i, ind + 1, n) {
			if (v[i][k]) {
				v[i][k] = 0;
				REP(j, k + 1, m) {
					v[i][j] ^= v[ind][j];
				}
			}
		}
		ind++;
	}
	ll v1 = 1ll, v2 = 1ll;
	REP(i, 0, n + m - 1) {
		v1 = (v1 * 2) % MOD;
	}
	REP(i, 0, n + m - ind - 1) {
		v2 = (v2 * 2) % MOD;
	}
	ll ans = (v1 + MOD - v2) % MOD;
	cout << ans << endl;
}   

