int elementaryMatrix(vector<bitset<300> >& mat)
{
    const int n = mat.size();
    const int m = mat[0].size();
    int y = 0;
    int x = 0;
    while(y < n && x < m){
        int tmp = y;
        while(tmp < n && !mat[tmp][x])
            ++ tmp;

        if(tmp < n){
            swap(mat[y], mat[tmp]);
            for(int i=y+1; i<n; ++i){
                if(mat[i][x])
                    mat[i] ^= mat[y];
            }
            ++ y;
        }
        ++ x;
    }
    mat.resize(y);
    return y;
}

const int MOD = 998244353;

long long power(long long a, int b)
{
    long long ret = 1;
    long long tmp = a;
    while(b > 0){
        if(b & 1){
            ret *= tmp;
            ret %= MOD;
        }
        tmp *= tmp;
        tmp %= MOD;
        b >>= 1;
    }
    return ret;
}

